import React, { useRef, useState } from 'react';
import axios from 'axios';

const VoiceUploader = () => {
  const mediaRecorderRef = useRef(null);
  const chunks = useRef([]);
  const [recording, setRecording] = useState(false);
  const [response, setResponse] = useState(null);

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const recorder = new MediaRecorder(stream);

    recorder.ondataavailable = (e) => chunks.current.push(e.data);
    recorder.onstop = () => {
      const blob = new Blob(chunks.current, { type: 'audio/wav' });
      chunks.current = [];
      uploadAudio(blob);
    };

    recorder.start();
    mediaRecorderRef.current = recorder;
    setRecording(true);
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
      setRecording(false);
    }
  };

  const uploadAudio = async (blob) => {
    const formData = new FormData();
    formData.append("file", blob, "voice.wav");

    try {
      const res = await axios.post("http://localhost:8080/api/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" }
      });
      setResponse(res.data);
    } catch (err) {
      console.error("上傳失敗：", err);
      alert("上傳失敗！");
    }
  };

  const formatTime = (isoString) => {
    const date = new Date(isoString);
    return date.toLocaleString('zh-TW', { hour12: false });
  };

  const getCategoryColor = (category) => {
    switch (category) {
      case "吃飯": return "#4caf50";
      case "交通": return "#2196f3";
      case "娛樂": return "#9c27b0";
      case "購物": return "#ff9800";
      default: return "#9e9e9e";
    }
  };

  return (
    <div style={{ padding: 20, fontFamily: 'sans-serif' }}>
      <h2>🎤 語音記帳系統</h2>

      <button
        onMouseDown={startRecording}
        onMouseUp={stopRecording}
        onTouchStart={startRecording}
        onTouchEnd={stopRecording}
        style={{
          padding: "10px 20px",
          fontSize: 18,
          backgroundColor: recording ? "#f44336" : "#4caf50",
          color: "white",
          border: "none",
          borderRadius: 8,
          cursor: "pointer"
        }}
      >
        {recording ? "錄音中..." : "按住錄音"}
      </button>

      {response && (
        <div
          style={{
            marginTop: 30,
            padding: 20,
            border: "1px solid #ccc",
            borderRadius: 10,
            maxWidth: 400,
            boxShadow: "0 0 10px rgba(0,0,0,0.1)"
          }}
        >
          <h3>✅ 紀錄成功</h3>
          <p><strong>📝 描述：</strong>{response.description || "(空白)"}</p>
          <p>
            <strong>📂 類別：</strong>
            <span style={{
              backgroundColor: getCategoryColor(response.category),
              color: "white",
              padding: "3px 8px",
              borderRadius: 5,
              fontSize: 14
            }}>
              {response.category}
            </span>
          </p>
          <p><strong>💰 金額：</strong>{response.amount} 元</p>
          <p><strong>📅 時間：</strong>{formatTime(response.time)}</p>
        </div>
      )}
    </div>
  );
};

export default VoiceUploader;
