import React, { useRef, useState } from 'react';
import axios from 'axios';

const VoiceUploader = () => {
  const mediaRecorderRef = useRef(null);
  const [audioBlob, setAudioBlob] = useState(null);
  const [recording, setRecording] = useState(false);
  const [response, setResponse] = useState(null);

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const recorder = new MediaRecorder(stream);
    const chunks = [];

    recorder.ondataavailable = (e) => chunks.push(e.data);
    recorder.onstop = () => {
      const blob = new Blob(chunks, { type: 'audio/wav' });
      setAudioBlob(blob);
    };

    recorder.start();
    mediaRecorderRef.current = recorder;
    setRecording(true);
  };

  const stopRecording = () => {
    mediaRecorderRef.current.stop();
    setRecording(false);
  };

  const uploadAudio = async () => {
    if (!audioBlob) return alert("Ë´ãÂÖàÈåÑÈü≥");
    const formData = new FormData();
    formData.append("file", audioBlob, "voice.wav");

    try {
      const res = await axios.post("http://localhost:8080/api/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" }
      });
      setResponse(res.data);
    } catch (err) {
      console.error("‰∏äÂÇ≥Â§±ÊïóÔºö", err);
      alert("‰∏äÂÇ≥Â§±ÊïóÔºÅ");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>üé§ Ë™ûÈü≥Ë®òÂ∏≥Á≥ªÁµ±</h2>
      <button onClick={recording ? stopRecording : startRecording}>
        {recording ? "‚èπ ÂÅúÊ≠¢ÈåÑÈü≥" : "üéô ÈñãÂßãÈåÑÈü≥"}
      </button>
      <button onClick={uploadAudio} disabled={!audioBlob} style={{ marginLeft: 10 }}>
        üì§ ‰∏äÂÇ≥Ë™ûÈü≥
      </button>
      {response && (
        <div style={{ marginTop: 20 }}>
          <h4>‰º∫ÊúçÂô®ÂõûÊáâÔºö</h4>
          <pre>{JSON.stringify(response, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default VoiceUploader;
