import React, { useRef, useState } from 'react';
import axios from 'axios';

const getCategoryColor = (category) => {
  const map = {
    'È£≤È£ü': 'green',
    '‰∫§ÈÄö': 'blue',
    'Â®õÊ®Ç': 'purple',
    'Ë≥ºÁâ©': 'orange',
    'ÂÖ∂‰ªñ': 'gray',
    'Êú™ÂàÜÈ°û': 'gray'
  };
  return map[category] || 'black';
};

const formatTime = (iso) => {
  if (!iso) return "Â∞öÊú™ÂÑ≤Â≠ò";
  return new Date(iso).toLocaleString('zh-TW');
};

const VoiceUploader = () => {
  const mediaRecorderRef = useRef(null);
  const [recording, setRecording] = useState(false);
  const [response, setResponse] = useState(null);
  const [records, setRecords] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [year, setYear] = useState('');
  const [month, setMonth] = useState('');
  const [day, setDay] = useState('');

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      const chunks = [];

      recorder.ondataavailable = (e) => chunks.push(e.data);
      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'audio/webm' });
        uploadAudio(blob);
      };

      recorder.start();
      mediaRecorderRef.current = recorder;
      setRecording(true);
    } catch (err) {
      alert("üé§ È∫•ÂÖãÈ¢®ÊéàÊ¨äÂ§±ÊïóÔºö" + err.message);
    }
  };

  const stopRecording = () => {
    const recorder = mediaRecorderRef.current;
    if (recorder && recorder.state !== 'inactive') {
      recorder.stop();
      setRecording(false);
    }
  };

  const uploadAudio = async (blob) => {
    const formData = new FormData();
    formData.append("file", blob, "voice.webm");

    try {
      const res = await axios.post("http://192.168.194.194:8080/api/upload", formData);
      setResponse(res.data);
      fetchRecords(); // Ëá™ÂãïÂà∑Êñ∞Á¥ÄÈåÑ
    } catch (err) {
      console.error("‰∏äÂÇ≥Â§±ÊïóÔºö", err);
      alert("‰∏äÂÇ≥Â§±ÊïóÔºÅ");
    }
  };

  const fetchRecords = async () => {
    const params = {};
    if (year) params.year = year;
    if (month) params.month = month;
    if (day) params.day = day;

    try {
      const res = await axios.get("http://192.168.194.194:8080/api/records/summary", { params });
      setRecords(res.data.records || []);
      setTotalAmount(res.data.total || 0);
    } catch (err) {
      console.error("Êü•Ë©¢Â§±ÊïóÔºö", err);
      alert("Êü•Ë©¢Â§±ÊïóÔºÅ");
    }
  };

  const updateRecord = async (id, updated) => {
    try {
      await axios.patch(`http://192.168.194.194:8080/api/records/${id}`, updated);
      fetchRecords();
      alert("‚úÖ ‰øÆÊîπÊàêÂäüÔºÅ");
    } catch (err) {
      alert("‚ùå ‰øÆÊîπÂ§±Êïó");
    }
  };

  const deleteRecord = async (id) => {
    if (!window.confirm("Á¢∫ÂÆöË¶ÅÂà™Èô§ÈÄôÁ≠ÜË®òÈåÑÂóéÔºü")) return;
    try {
      await axios.delete(`http://192.168.194.194:8080/api/records/${id}`);
      fetchRecords();
    } catch (err) {
      alert("‚ùå Âà™Èô§Â§±Êïó");
    }
  };

  return (
    <div style={{ padding: 20, maxWidth: 700, margin: 'auto' }}>
      <h2>üé§ Ë™ûÈü≥Ë®òÂ∏≥Á≥ªÁµ±</h2>

      <button
        onMouseDown={startRecording}
        onMouseUp={stopRecording}
        onTouchStart={startRecording}
        onTouchEnd={stopRecording}
        style={{ padding: 10, background: 'green', color: 'white', borderRadius: 8 }}
      >
        {recording ? "ÈåÑÈü≥‰∏≠..." : "üéô Êåâ‰ΩèÈåÑÈü≥"}
      </button>

      {response && (
        <div style={{ marginTop: 20, padding: 15, border: '1px solid #ccc', borderRadius: 8, background: '#f9f9f9' }}>
          <h3>‚úÖ Á¥ÄÈåÑÊàêÂäü</h3>
          <p><strong>üìù ÊèèËø∞Ôºö</strong>{response.description || "(Á©∫ÁôΩ)"}</p>
          <p><strong>üìÇ È°ûÂà•Ôºö</strong>{response.category}</p>
          <p><strong>üí∞ ÈáëÈ°çÔºö</strong>{response.amount} ÂÖÉ</p>
          <p><strong>üìÖ ÊôÇÈñìÔºö</strong>{formatTime(response.time)}</p>
        </div>
      )}

      <hr style={{ margin: '30px 0' }} />

      <h3>üìä Êü•Ë©¢Á¥ÄÈåÑ</h3>
      <div style={{ marginBottom: 10 }}>
        Âπ¥Ôºö<input value={year} onChange={(e) => setYear(e.target.value)} style={{ width: 60 }} />
        ÊúàÔºö<input value={month} onChange={(e) => setMonth(e.target.value)} style={{ width: 40, marginLeft: 10 }} />
        Êó•Ôºö<input value={day} onChange={(e) => setDay(e.target.value)} style={{ width: 40, marginLeft: 10 }} />
        <button onClick={fetchRecords} style={{ marginLeft: 10 }}>Êü•Ë©¢</button>
      </div>

      <p><strong>üíµ Á∏ΩÊ∂àË≤ªÔºö</strong>{totalAmount} ÂÖÉ</p>

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {records.map(r => (
          <li key={r.id} style={{ margin: '10px 0', border: '1px solid #ccc', borderRadius: 5, padding: 10 }}>
            <input
              value={r.description}
              disabled
              style={{ fontWeight: 'bold', width: '100%', border: 'none', marginBottom: 8 }}
            />
            <div>
              üìÇ
              <select
                value={r.category}
                onChange={(e) => setRecords(records.map(item => item.id === r.id ? { ...item, category: e.target.value } : item))}
              >
                <option value="È£≤È£ü">È£≤È£ü</option>
                <option value="‰∫§ÈÄö">‰∫§ÈÄö</option>
                <option value="Â®õÊ®Ç">Â®õÊ®Ç</option>
                <option value="Ë≥ºÁâ©">Ë≥ºÁâ©</option>
                <option value="ÂÖ∂‰ªñ">ÂÖ∂‰ªñ</option>
              </select>
              &nbsp; üí∞
              <input
                type="number"
                value={r.amount}
                onChange={(e) => setRecords(records.map(item => item.id === r.id ? { ...item, amount: parseInt(e.target.value) || 0 } : item))}
                style={{ width: 60 }}
              />
              &nbsp; üïí {formatTime(r.time)}
            </div>
            <div style={{ marginTop: 5 }}>
              <button onClick={() => updateRecord(r.id, { category: r.category, amount: r.amount })}>üíæ ÂÑ≤Â≠ò‰øÆÊîπ</button>
              <button onClick={() => deleteRecord(r.id)} style={{ marginLeft: 10, color: 'red' }}>üóë Âà™Èô§</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default VoiceUploader;
