// VoiceBudgetApp.js (圖表整合版 - 含日期下拉、圖表、語音上傳、手動修改)
import React, { useRef, useState, useEffect } from 'react';
import axios from 'axios';
import {
  PieChart, Pie, Cell, Tooltip as PieTooltip,
  LineChart, Line, XAxis, YAxis, CartesianGrid, Legend
} from 'recharts';

const getCategoryColor = (category) => {
  const map = {
    '飲食': 'green', '交通': 'blue', '娛樂': 'purple', '購物': 'orange',
    '收入': 'teal', '其他': 'gray', '未分類': 'black'
  };
  return map[category] || 'black';
};
const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#FF6666', '#8884d8'];
const formatTime = (iso) => new Date(iso).toLocaleString('zh-TW');
const formatDate = (iso) => {
  const d = new Date(iso);
  return { year: d.getFullYear(), month: d.getMonth() + 1, day: d.getDate() };
};
const getDaysInMonth = (year, month) => {
  if (!year || !month) return 31;
  return new Date(year, month, 0).getDate();
};

const VoiceBudgetApp = () => {
  const mediaRecorderRef = useRef(null);
  const [recording, setRecording] = useState(false);
  const [response, setResponse] = useState(null);
  const [records, setRecords] = useState([]);
  const [income, setIncome] = useState(0);
  const [expense, setExpense] = useState(0);
  const [total, setTotal] = useState(0);
  const [year, setYear] = useState('');
  const [month, setMonth] = useState('');
  const [day, setDay] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editDescription, setEditDescription] = useState('');
  const [editAmount, setEditAmount] = useState(0);
  const [editCategory, setEditCategory] = useState('');
  const [editType, setEditType] = useState('');
  const [editTimeYear, setEditTimeYear] = useState('');
  const [editTimeMonth, setEditTimeMonth] = useState('');
  const [editTimeDay, setEditTimeDay] = useState('');

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const recorder = new MediaRecorder(stream);
    const chunks = [];
    recorder.ondataavailable = (e) => chunks.push(e.data);
    recorder.onstop = () => uploadAudio(new Blob(chunks, { type: 'audio/webm' }));
    recorder.start();
    mediaRecorderRef.current = recorder;
    setRecording(true);
  };
  const stopRecording = () => {
    const r = mediaRecorderRef.current;
    if (r && r.state !== 'inactive') {
      r.stop();
      setRecording(false);
    }
  };
  const uploadAudio = async (blob) => {
    const formData = new FormData();
    formData.append("file", blob, "voice.webm");
    const res = await axios.post("http://192.168.194.194:8080/api/upload", formData);
    setResponse(res.data);
    fetchRecords();
  };
  const fetchRecords = async () => {
    const res = await axios.get("http://192.168.194.194:8080/api/records/summary", {
      params: { year, month, day }
    });
    setRecords(res.data.records);
    setIncome(res.data.income); setExpense(res.data.expense); setTotal(res.data.total);
  };
  const startEdit = (r) => {
    const { year, month, day } = formatDate(r.time);
    setEditingId(r.id);
    setEditDescription(r.description);
    setEditAmount(r.amount);
    setEditCategory(r.category);
    setEditType(r.type);
    setEditTimeYear(year);
    setEditTimeMonth(month);
    setEditTimeDay(day);
  };
  const saveEdit = async () => {
    const y = parseInt(editTimeYear) || 2025;
    const m = parseInt(editTimeMonth) || 1;
    const d = parseInt(editTimeDay) || 1;
    const fixedTime = new Date(y, m - 1, d, 12).toISOString();
    await axios.put(`http://192.168.194.194:8080/api/records/${editingId}`, {
      description: editDescription,
      amount: parseInt(editAmount),
      category: editCategory,
      type: editType,
      time: fixedTime
    });
    setEditingId(null);
    fetchRecords();
  };
  const deleteRecord = async (id) => {
    if (window.confirm("確定刪除？")) {
      await axios.delete(`http://192.168.194.194:8080/api/records/${id}`);
      fetchRecords();
    }
  };
  const getCategorySummaryByType = (type) => {
    const s = {};
    records.filter(r => r.type === type).forEach(r => {
      if (!s[r.category]) s[r.category] = 0;
      s[r.category] += r.amount;
    });
    return Object.entries(s).map(([category, amount]) => ({ category, amount }));
  };
  const getDailyIncomeExpense = () => {
    const r = {};
    records.forEach(e => {
      const d = new Date(e.time).toLocaleDateString();
      if (!r[d]) r[d] = { date: d, 收入: 0, 支出: 0 };
      r[d][e.type] += e.amount;
    });
    return Object.values(r);
  };
  useEffect(() => { fetchRecords(); }, []);

  return (
    <div style={{ padding: 20, maxWidth: 900, margin: 'auto' }}>
      <h2>🎤 語音記帳系統</h2>
      <button
        onMouseDown={startRecording} onMouseUp={stopRecording}
        onTouchStart={startRecording} onTouchEnd={stopRecording}
        style={{ padding: 10, background: 'green', color: 'white', borderRadius: 8 }}
      >
        {recording ? "錄音中..." : "🎙 按住錄音"}
      </button>

      <div style={{ marginTop: 20 }}>
        年：
        <select value={year} onChange={e => setYear(e.target.value)}>
          <option value="">全部</option>
          {Array.from({ length: 11 }, (_, i) => 2020 + i).map(y => (
            <option key={y} value={y}>{y}</option>
          ))}
        </select>
        月：
        <select value={month} onChange={e => setMonth(e.target.value)}>
          <option value="">全部</option>
          {Array.from({ length: 12 }, (_, i) => i + 1).map(m => (
            <option key={m} value={m}>{m}</option>
          ))}
        </select>
        日：
        <select value={day} onChange={e => setDay(e.target.value)}>
          <option value="">全部</option>
          {Array.from({ length: getDaysInMonth(year, month) }, (_, i) => i + 1).map(d => (
            <option key={d} value={d}>{d}</option>
          ))}
        </select>
        <button onClick={fetchRecords} style={{ marginLeft: 10 }}>查詢</button>
      </div>

      <p>💰 收入總和：{income} 元</p>
      <p>🧾 支出總和：{expense} 元</p>
      <p>💵 淨額：{total} 元</p>

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {records.map(r => (
          <li key={r.id} style={{ border: '1px solid #ccc', marginBottom: 10, borderRadius: 8, padding: 10 }}>
            {editingId === r.id ? (
              <div>
                <input value={editDescription} onChange={e => setEditDescription(e.target.value)} />
                <input value={editAmount} onChange={e => setEditAmount(e.target.value)} />
                <select value={editCategory} onChange={e => setEditCategory(e.target.value)}>
                  <option>飲食</option><option>交通</option><option>娛樂</option><option>購物</option><option>其他</option>
                </select>
                <select value={editType} onChange={e => setEditType(e.target.value)}>
                  <option>支出</option><option>收入</option>
                </select>
                <select value={editTimeYear} onChange={e => setEditTimeYear(e.target.value)}>
                  {Array.from({ length: 11 }, (_, i) => 2020 + i).map(y => (
                    <option key={y} value={y}>{y}</option>
                  ))}
                </select>
                <select value={editTimeMonth} onChange={e => setEditTimeMonth(e.target.value)}>
                  {Array.from({ length: 12 }, (_, i) => i + 1).map(m => (
                    <option key={m} value={m}>{m}</option>
                  ))}
                </select>
                <select value={editTimeDay} onChange={e => setEditTimeDay(e.target.value)}>
                  {Array.from({ length: getDaysInMonth(editTimeYear, editTimeMonth) }, (_, i) => i + 1).map(d => (
                    <option key={d} value={d}>{d}</option>
                  ))}
                </select>
                <button onClick={saveEdit}>💾</button>
                <button onClick={() => setEditingId(null)}>取消</button>
              </div>
            ) : (
              <>
                <p><b>{r.description}</b></p>
                <p>📂 <span style={{ color: getCategoryColor(r.category) }}>{r.category}</span> | 💰 {r.amount} 元 | 🕒 {formatTime(r.time)}</p>
                <button onClick={() => startEdit(r)}>✏️</button>
                <button onClick={() => deleteRecord(r.id)}>🗑</button>
              </>
            )}
          </li>
        ))}
      </ul>

      <hr />
      <h3>📊 圖表分析</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 30 }}>
        <div style={{ flex: 1 }}>
          <h4>支出分類占比</h4>
          <PieChart width={300} height={250}>
            <Pie data={getCategorySummaryByType("支出")} dataKey="amount" nameKey="category" outerRadius={100}>
              {getCategorySummaryByType("支出").map((entry, index) => (
                <Cell key={index} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <PieTooltip />
          </PieChart>
        </div>

        <div style={{ flex: 1 }}>
          <h4>收入分類占比</h4>
          <PieChart width={300} height={250}>
            <Pie data={getCategorySummaryByType("收入")} dataKey="amount" nameKey="category" outerRadius={100}>
              {getCategorySummaryByType("收入").map((entry, index) => (
                <Cell key={index} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <PieTooltip />
          </PieChart>
        </div>
      </div>

      <h4>📈 每日趨勢</h4>
      <LineChart width={600} height={300} data={getDailyIncomeExpense()}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="date" />
        <YAxis />
        <Legend />
        <Tooltip />
        <Line type="monotone" dataKey="收入" stroke="#00C49F" />
        <Line type="monotone" dataKey="支出" stroke="#FF8042" />
      </LineChart>
    </div>
  );
};

export default VoiceBudgetApp;
