import React, { useRef, useState } from 'react';
import axios from 'axios';
import {
  PieChart, Pie, Cell, Tooltip as PieTooltip,
  LineChart, Line, XAxis, YAxis, CartesianGrid, Legend,
  ResponsiveContainer
} from 'recharts';

const getCategoryColor = (category) => {
  const map = {
    'È£≤È£ü': 'green',
    '‰∫§ÈÄö': 'blue',
    'Â®õÊ®Ç': 'purple',
    'Ë≥ºÁâ©': 'orange',
    'Êî∂ÂÖ•': 'teal',
    'ÂÖ∂‰ªñ': 'gray',
    'Êú™ÂàÜÈ°û': 'black'
  };
  return map[category] || 'black';
};

const formatTime = (iso) => new Date(iso).toLocaleString('zh-TW');
const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#FF6666', '#8884d8'];

const VoiceBudgetApp = () => {
  const mediaRecorderRef = useRef(null);
  const [recording, setRecording] = useState(false);
  const [response, setResponse] = useState(null);
  const [records, setRecords] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [income, setIncome] = useState(0);
  const [expense, setExpense] = useState(0);
  const [year, setYear] = useState('');
  const [month, setMonth] = useState('');
  const [day, setDay] = useState('');

  const [editId, setEditId] = useState(null);
  const [editDesc, setEditDesc] = useState('');
  const [editAmount, setEditAmount] = useState('');
  const [editCategory, setEditCategory] = useState('');

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      const chunks = [];

      recorder.ondataavailable = (e) => chunks.push(e.data);
      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'audio/webm' });
        uploadAudio(blob);
      };

      recorder.start();
      mediaRecorderRef.current = recorder;
      setRecording(true);
    } catch (err) {
      alert("üé§ È∫•ÂÖãÈ¢®ÊéàÊ¨äÂ§±ÊïóÔºö" + err.message);
    }
  };

  const stopRecording = () => {
    const recorder = mediaRecorderRef.current;
    if (recorder && recorder.state !== 'inactive') {
      recorder.stop();
      setRecording(false);
    }
  };

  const uploadAudio = async (blob) => {
    const formData = new FormData();
    formData.append("file", blob, "voice.webm");

    try {
      const res = await axios.post("http://192.168.194.194:8080/api/upload", formData);
      setResponse(res.data);
      fetchRecords();
    } catch (err) {
      alert("‰∏äÂÇ≥Â§±ÊïóÔºÅ");
    }
  };

  const fetchRecords = async () => {
    const params = {};
    if (year) params.year = year;
    if (month) params.month = month;
    if (day) params.day = day;

    try {
      const res = await axios.get("http://192.168.194.194:8080/api/records/summary", { params });
      setRecords(res.data.records || []);
      setTotalAmount(res.data.total || 0);
      setIncome(res.data.income || 0);
      setExpense(res.data.expense || 0);
    } catch (err) {
      alert("Êü•Ë©¢Â§±ÊïóÔºÅ");
    }
  };

  const deleteRecord = async (id) => {
    if (!window.confirm("Á¢∫ÂÆöË¶ÅÂà™Èô§ÂóéÔºü")) return;
    await axios.delete(`http://192.168.194.194:8080/api/records/${id}`);
    fetchRecords();
  };

  const startEdit = (r) => {
    setEditId(r.id);
    setEditDesc(r.description);
    setEditAmount(r.amount);
    setEditCategory(r.category);
  };

  const saveEdit = async () => {
    await axios.put(`http://192.168.194.194:8080/api/records/${editId}`, {
      description: editDesc,
      amount: editAmount,
      category: editCategory
    });
    setEditId(null);
    fetchRecords();
  };

  const getCategorySummary = () => {
    const summary = {};
    records.forEach(r => {
      if (!summary[r.category]) summary[r.category] = 0;
      summary[r.category] += r.amount;
    });
    return Object.entries(summary).map(([category, amount]) => ({ category, amount }));
  };

  const getDailySummary = () => {
    const byDay = {};
    records.forEach(r => {
      const d = new Date(r.time).toLocaleDateString();
      if (!byDay[d]) byDay[d] = 0;
      byDay[d] += r.amount;
    });
    return Object.entries(byDay).map(([date, amount]) => ({ date, amount }));
  };

  return (
    <div style={{ padding: 20, maxWidth: 900, margin: 'auto' }}>
      <h2>üé§ Ë™ûÈü≥Ë®òÂ∏≥Á≥ªÁµ±</h2>

      <button
        onMouseDown={startRecording}
        onMouseUp={stopRecording}
        onTouchStart={startRecording}
        onTouchEnd={stopRecording}
        style={{ padding: 10, background: 'green', color: 'white', borderRadius: 8 }}
      >
        {recording ? "ÈåÑÈü≥‰∏≠..." : "üéô Êåâ‰ΩèÈåÑÈü≥"}
      </button>

      {response && (
        <div style={{ marginTop: 20, padding: 15, border: '1px solid #ccc', borderRadius: 8, background: '#f9f9f9' }}>
          <h3>‚úÖ Á¥ÄÈåÑÊàêÂäü</h3>
          <p><strong>üìù ÊèèËø∞Ôºö</strong>{response.description || "(Á©∫ÁôΩ)"}</p>
          <p>
            <strong>üìÇ È°ûÂà•Ôºö</strong>
            <span style={{ backgroundColor: getCategoryColor(response.category), color: "white", padding: '3px 8px', borderRadius: 5 }}>{response.category}</span>
          </p>
          <p><strong>üí∞ ÈáëÈ°çÔºö</strong>{response.amount} ÂÖÉ</p>
          <p><strong>üìÖ ÊôÇÈñìÔºö</strong>{formatTime(response.time)}</p>
        </div>
      )}

      <hr style={{ margin: '30px 0' }} />
      <h3>üìä Êü•Ë©¢Á¥ÄÈåÑ</h3>
      <div>
        Âπ¥Ôºö<input value={year} onChange={(e) => setYear(e.target.value)} style={{ width: 60 }} />
        ÊúàÔºö<input value={month} onChange={(e) => setMonth(e.target.value)} style={{ width: 40 }} />
        Êó•Ôºö<input value={day} onChange={(e) => setDay(e.target.value)} style={{ width: 40 }} />
        <button onClick={fetchRecords} style={{ marginLeft: 10 }}>Êü•Ë©¢</button>
      </div>

      <p>
        üí∞ <b>Êî∂ÂÖ•Ôºö</b>{income} ÂÖÉ„ÄÄ|„ÄÄ
        üßæ <b>ÊîØÂá∫Ôºö</b>{expense} ÂÖÉ„ÄÄ|„ÄÄ
        üíµ <b>Ê∑®È°çÔºö</b>{income - expense} ÂÖÉ
      </p>

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {records.map(r => (
          <li key={r.id} style={{ margin: '10px 0', border: '1px solid #ccc', borderRadius: 5, padding: 10 }}>
            {editId === r.id ? (
              <>
                <input value={editDesc} onChange={(e) => setEditDesc(e.target.value)} />
                <input value={editAmount} onChange={(e) => setEditAmount(e.target.value)} style={{ width: 80, marginLeft: 10 }} />
                <select value={editCategory} onChange={(e) => setEditCategory(e.target.value)} style={{ marginLeft: 10 }}>
                  <option>È£≤È£ü</option>
                  <option>‰∫§ÈÄö</option>
                  <option>Â®õÊ®Ç</option>
                  <option>Ë≥ºÁâ©</option>
                  <option>ÂÖ∂‰ªñ</option>
                  <option>Êî∂ÂÖ•</option>
                </select>
                <button onClick={saveEdit} style={{ marginLeft: 10 }}>‚úÖ</button>
              </>
            ) : (
              <>
                <p><strong>{r.description}</strong></p>
                <p>
                  üìÇ <span style={{ color: getCategoryColor(r.category) }}>{r.category}</span> |
                  üí∞ {r.amount} ÂÖÉ |
                  üïí {formatTime(r.time)}
                </p>
                <button onClick={() => startEdit(r)}>‚úèÔ∏è</button>
                <button onClick={() => deleteRecord(r.id)} style={{ marginLeft: 5 }}>üóë</button>
              </>
            )}
          </li>
        ))}
      </ul>

      <hr style={{ margin: '30px 0' }} />
      <h3>üìà Ê∂àË≤ªÂúñË°®ÂàÜÊûê</h3>

      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 30 }}>
        <div style={{ flex: 1, minWidth: 300 }}>
          <h4>ÂàÜÈ°ûÂç†ÊØî</h4>
          <PieChart width={300} height={250}>
            <Pie data={getCategorySummary()} dataKey="amount" nameKey="category" outerRadius={100}>
              {getCategorySummary().map((entry, index) => (
                <Cell key={index} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <PieTooltip />
          </PieChart>
        </div>

        <div style={{ flex: 2, minWidth: 400 }}>
          <h4>ÊØèÊó•Á∏ΩËä±Ë≤ª</h4>
          <LineChart width={400} height={250} data={getDailySummary()}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" />
            <YAxis />
            <Legend />
