        onMouseUp={stopRecording}
        onTouchStart={startRecording}
        onTouchEnd={stopRecording}
        style={{ padding: 10, background: 'green', color: 'white', borderRadius: 8 }}
      >
        {recording ? "ÈåÑÈü≥‰∏≠..." : "üéô Êåâ‰ΩèÈåÑÈü≥"}
      </button>

      {response && (
        <div style={{ marginTop: 20, padding: 15, border: '1px solid #ccc', borderRadius: 8, background: '#f9f9f9' }}>
          <h3>‚úÖ Á¥ÄÈåÑÊàêÂäü</h3>
          <p><strong>üìù ÊèèËø∞Ôºö</strong>{response.description || "(Á©∫ÁôΩ)"}</p>
          <p><strong>üìÇ È°ûÂà•Ôºö</strong><span style={{ backgroundColor: getCategoryColor(response.category), color: "white", padding: '3px 8px', borderRadius: 5 }}>{response.category}</span></p>
          <p><strong>üí∞ ÈáëÈ°çÔºö</strong>{response.amount} ÂÖÉ</p>
          <p><strong>üìÖ ÊôÇÈñìÔºö</strong>{formatTime(response.time)}</p>
        </div>
      )}

      <hr style={{ margin: '30px 0' }} />

      <h3>üìä Êü•Ë©¢Á¥ÄÈåÑ</h3>
      <div style={{ marginBottom: 10 }}>
        Âπ¥Ôºö
        <select value={year} onChange={(e) => setYear(e.target.value)}>
          <option value="">--</option>
          {Array.from({ length: 11 }, (_, i) => 2020 + i).map(y => (
            <option key={y} value={y}>{y}</option>
          ))}
        </select>
        ÊúàÔºö
        <select value={month} onChange={(e) => setMonth(e.target.value)} style={{ marginLeft: 10 }}>
          <option value="">--</option>
          {Array.from({ length: 12 }, (_, i) => i + 1).map(m => (
            <option key={m} value={m}>{m}</option>
          ))}
        </select>
        Êó•Ôºö
        <select value={day} onChange={(e) => setDay(e.target.value)} style={{ marginLeft: 10 }}>
          <option value="">--</option>
          {Array.from({ length: getDaysInMonth(year, month) }, (_, i) => i + 1).map(d => (
            <option key={d} value={d}>{d}</option>
          ))}
        </select>
        <button onClick={fetchRecords} style={{ marginLeft: 10 }}>Êü•Ë©¢</button>
      </div>

      <p><strong>üí∞ Êî∂ÂÖ•Á∏ΩÂíåÔºö</strong>{income} ÂÖÉ</p>
      <p><strong>üßæ ÊîØÂá∫Á∏ΩÂíåÔºö</strong>{expense} ÂÖÉ</p>
      <p><strong>üíµ Ê∑®È°çÔºö</strong>{total} ÂÖÉ</p>

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {records.map(r => (
          <li key={r.id} style={{ margin: '10px 0', border: '1px solid #ccc', borderRadius: 5, padding: 10 }}>
            {editingId === r.id ? (
              <div>
                <input value={editDescription} onChange={(e) => setEditDescription(e.target.value)} />
                <input type="number" value={editAmount} onChange={(e) => setEditAmount(e.target.value)} />
                <select value={editCategory} onChange={(e) => setEditCategory(e.target.value)}>
                  <option>È£≤È£ü</option><option>‰∫§ÈÄö</option><option>Â®õÊ®Ç</option><option>Ë≥ºÁâ©</option><option>ÂÖ∂‰ªñ</option>
                </select>
                <select value={editType} onChange={(e) => setEditType(e.target.value)}>
                  <option>ÊîØÂá∫</option><option>Êî∂ÂÖ•</option>
                </select>
                <div>
                  Âπ¥Ôºö
                  <select value={editTimeYear} onChange={(e) => setEditTimeYear(e.target.value)}>
                    {Array.from({ length: 11 }, (_, i) => 2020 + i).map(y => (
                      <option key={y} value={y}>{y}</option>
                    ))}
                  </select>
                  ÊúàÔºö
                  <select value={editTimeMonth} onChange={(e) => setEditTimeMonth(e.target.value)} style={{ marginLeft: 10 }}>
                    {Array.from({ length: 12 }, (_, i) => i + 1).map(m => (
