                <button onClick={() => setEditingId(null)} style={{ marginLeft: 5 }}>ÂèñÊ∂à</button>
              </div>
            ) : (
              <div>
                <p><strong>{r.description}</strong></p>
                <p>üìÇ <span style={{ color: getCategoryColor(r.category) }}>{r.category}</span> | üí∞ {r.amount} ÂÖÉ | üïí {formatTime(r.time)}</p>
                <button onClick={() => startEdit(r)}>‚úèÔ∏è ‰øÆÊîπ</button>
                <button onClick={() => deleteRecord(r.id)} style={{ marginLeft: 5 }}>üóë Âà™Èô§</button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default VoiceBudgetApp;
