// VoiceBudgetApp.js
import React, { useRef, useState, useEffect } from 'react';
import axios from 'axios';

const getCategoryColor = (category) => {
  const map = {
    '飲食': 'green',
    '交通': 'blue',
    '娛樂': 'purple',
    '購物': 'orange',
    '其他': 'gray'
  };
  return map[category] || 'black';
};

const formatTime = (iso) => new Date(iso).toLocaleString('zh-TW');

const VoiceBudgetApp = () => {
  const mediaRecorderRef = useRef(null);
  const [recording, setRecording] = useState(false);
  const [response, setResponse] = useState(null);
  const [records, setRecords] = useState([]);
  const [total, setTotal] = useState(0);
  const [income, setIncome] = useState(0);
  const [expense, setExpense] = useState(0);
  const [year, setYear] = useState('');
  const [month, setMonth] = useState('');
  const [day, setDay] = useState('');
  const [editing, setEditing] = useState(null);
  const [editData, setEditData] = useState({});

  const years = Array.from({ length: 11 }, (_, i) => 2020 + i);
  const months = Array.from({ length: 12 }, (_, i) => i + 1);
  const days = Array.from({ length: 31 }, (_, i) => i + 1);

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const recorder = new MediaRecorder(stream);
    const chunks = [];

    recorder.ondataavailable = (e) => chunks.push(e.data);
    recorder.onstop = () => {
      const blob = new Blob(chunks, { type: 'audio/webm' });
      uploadAudio(blob);
    };

    recorder.start();
    mediaRecorderRef.current = recorder;
    setRecording(true);
  };

  const stopRecording = () => {
    const recorder = mediaRecorderRef.current;
    if (recorder && recorder.state !== 'inactive') {
      recorder.stop();
      setRecording(false);
    }
  };

  const uploadAudio = async (blob) => {
    const formData = new FormData();
    formData.append('file', blob, 'voice.webm');

    const res = await axios.post('http://localhost:8080/api/upload', formData);
    setResponse(res.data);
    fetchRecords();
  };

  const fetchRecords = async () => {
    const params = {};
    if (year) params.year = year;
    if (month) params.month = month;
    if (day) params.day = day;

    const res = await axios.get('http://localhost:8080/api/records/summary', { params });
    setRecords(res.data.records);
    setIncome(res.data.income);
    setExpense(res.data.expense);
    setTotal(res.data.total);
  };

  const handleEdit = (r) => {
    const date = new Date(r.time);
    setEditData({ ...r });
    setEditing(r.id);
    setEditData((prev) => ({
      ...prev,
      year: date.getFullYear(),
      month: date.getMonth() + 1,
      day: date.getDate()
    }));
  };

  const saveEdit = async () => {
    const date = new Date(`${editData.year}-${editData.month}-${editData.day}T00:00`);
    const updated = { ...editData, time: date.toISOString() };
    await axios.put(`http://localhost:8080/api/records/${editing}`, updated);
    setEditing(null);
    fetchRecords();
  };

  const deleteRecord = async (id) => {
    await axios.delete(`http://localhost:8080/api/records/${id}`);
    fetchRecords();
  };

  return (
    <div style={{ padding: 20, maxWidth: 800, margin: 'auto' }}>
      <h2>🎤 語音記帳系統</h2>

      <button
        onMouseDown={startRecording}
        onMouseUp={stopRecording}
        onTouchStart={startRecording}
        onTouchEnd={stopRecording}
        style={{ padding: 10, background: 'green', color: 'white', borderRadius: 8 }}
      >
        {recording ? "錄音中..." : "🎙 按住錄音"}
      </button>

      {response && (
        <div style={{ marginTop: 20, padding: 15, border: '1px solid #ccc', borderRadius: 8 }}>
          <h3>✅ 成功記錄</h3>
          <p>📝 {response.description}</p>
          <p>📂 {response.category} 💰 {response.amount} 元</p>
        </div>
      )}

      <hr />

      <h3>📊 查詢紀錄</h3>
      年：
      <select value={year} onChange={(e) => setYear(e.target.value)}>
        <option value="">全部</option>
        {years.map(y => <option key={y} value={y}>{y}</option>)}
      </select>
      月：
      <select value={month} onChange={(e) => setMonth(e.target.value)}>
        <option value="">全部</option>
        {months.map(m => <option key={m} value={m}>{m}</option>)}
      </select>
      日：
      <select value={day} onChange={(e) => setDay(e.target.value)}>
        <option value="">全部</option>
        {days.map(d => <option key={d} value={d}>{d}</option>)}
      </select>
      <button onClick={fetchRecords}>查詢</button>

      <p>💰 收入：{income} 元　🧾 支出：{expense} 元　💵 淨額：{total} 元</p>

      {records.map(r => (
        <div key={r.id} style={{ border: '1px solid #ccc', margin: 10, padding: 10, borderRadius: 6 }}>
          {editing === r.id ? (
            <>
              描述：<input value={editData.description} onChange={e => setEditData({ ...editData, description: e.target.value })} />
              金額：<input value={editData.amount} onChange={e => setEditData({ ...editData, amount: e.target.value })} />
              類別：
              <select value={editData.category} onChange={e => setEditData({ ...editData, category: e.target.value })}>
                <option>飲食</option>
                <option>交通</option>
                <option>娛樂</option>
                <option>購物</option>
                <option>其他</option>
              </select>
              類型：
              <select value={editData.type} onChange={e => setEditData({ ...editData, type: e.target.value })}>
                <option>支出</option>
                <option>收入</option>
              </select>
              日期：
              <select value={editData.year} onChange={e => setEditData({ ...editData, year: e.target.value })}>
                {years.map(y => <option key={y} value={y}>{y}</option>)}
              </select>年
              <select value={editData.month} onChange={e => setEditData({ ...editData, month: e.target.value })}>
                {months.map(m => <option key={m} value={m}>{m}</option>)}
              </select>月
              <select value={editData.day} onChange={e => setEditData({ ...editData, day: e.target.value })}>
                {days.map(d => <option key={d} value={d}>{d}</option>)}
              </select>日
              <br />
              <button onClick={saveEdit}>儲存</button>
              <button onClick={() => setEditing(null)}>取消</button>
            </>
          ) : (
            <>
              <p><strong>{r.description}</strong> 💰 {r.amount} 元</p>
              <p>📂 {r.category} | {r.type} | 🕒 {formatTime(r.time)}</p>
              <button onClick={() => handleEdit(r)}>✏️ 編輯</button>
              <button onClick={() => deleteRecord(r.id)}>🗑️ 刪除</button>
            </>
          )}
        </div>
      ))}
    </div>
  );
};

export default VoiceBudgetApp;
