import React, { useRef, useState, useEffect } from 'react';
import axios from 'axios';
import {
  PieChart, Pie, Cell, Tooltip,
  LineChart, Line, XAxis, YAxis, CartesianGrid, Legend
} from 'recharts';

const getCategoryColor = (category) => {
  const map = {
    'È£≤È£ü': '#4CAF50',
    '‰∫§ÈÄö': '#2196F3',
    'Â®õÊ®Ç': '#9C27B0',
    'Ë≥ºÁâ©': '#FF9800',
    'ÂÖ∂‰ªñ': '#9E9E9E'
  };
  return map[category] || '#000';
};

const VoiceBudgetApp = () => {
  const mediaRecorderRef = useRef(null);
  const [recording, setRecording] = useState(false);
  const [response, setResponse] = useState(null);
  const [records, setRecords] = useState([]);
  const [year, setYear] = useState('');
  const [month, setMonth] = useState('');
  const [day, setDay] = useState('');
  const [income, setIncome] = useState(0);
  const [expense, setExpense] = useState(0);
  const [total, setTotal] = useState(0);
  const [editing, setEditing] = useState(null);
  const [editData, setEditData] = useState({});

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const recorder = new MediaRecorder(stream);
    const chunks = [];
    recorder.ondataavailable = (e) => chunks.push(e.data);
    recorder.onstop = () => {
      const blob = new Blob(chunks, { type: 'audio/webm' });
      uploadAudio(blob);
    };
    recorder.start();
    mediaRecorderRef.current = recorder;
    setRecording(true);
  };

  const stopRecording = () => {
    mediaRecorderRef.current?.stop();
    setRecording(false);
  };

  const uploadAudio = async (blob) => {
    const formData = new FormData();
    formData.append('file', blob, 'voice.webm');
    const res = await axios.post('http://localhost:8080/api/upload', formData);
    setResponse(res.data);
    fetchRecords(); // refresh
  };

  const fetchRecords = async () => {
    const params = {};
    if (year) params.year = year;
    if (month) params.month = month;
    if (day) params.day = day;
    const res = await axios.get('http://localhost:8080/api/records/summary', { params });
    setRecords(res.data.records || []);
    setIncome(res.data.income || 0);
    setExpense(res.data.expense || 0);
    setTotal(res.data.total || 0);
  };

  const saveEdit = async (id) => {
    await axios.put(`http://localhost:8080/api/records/${id}`, editData);
    setEditing(null);
    fetchRecords();
  };

  const deleteRecord = async (id) => {
    await axios.delete(`http://localhost:8080/api/records/${id}`);
    fetchRecords();
  };

  const handleEdit = (record) => {
    setEditing(record.id);
    setEditData({ ...record, time: record.time.substring(0, 10) });
  };

  const handleChange = (key, value) => {
    setEditData({ ...editData, [key]: value });
  };

  const formatTime = (iso) => new Date(iso).toLocaleString('zh-TW');

  const chartData = records.reduce((acc, r) => {
    const entry = acc.find(e => e.name === r.category);
    if (entry) entry.value += r.amount;
    else acc.push({ name: r.category, value: r.amount });
    return acc;
  }, []);

  const lineData = records.map(r => ({
    date: r.time.substring(0, 10),
    amount: r.amount * (r.type === 'Êî∂ÂÖ•' ? 1 : -1)
  }));

  return (
    <div style={{ padding: 20, maxWidth: 900, margin: 'auto' }}>
      <h2>üé§ Ë™ûÈü≥Ë®òÂ∏≥Á≥ªÁµ±</h2>

      <button
        onMouseDown={startRecording}
        onMouseUp={stopRecording}
        onTouchStart={startRecording}
        onTouchEnd={stopRecording}
        style={{ padding: 10, background: 'green', color: 'white', borderRadius: 8 }}
      >
        {recording ? "ÈåÑÈü≥‰∏≠..." : "üéô Êåâ‰ΩèÈåÑÈü≥"}
      </button>

      {response && (
        <div style={{ marginTop: 20, padding: 15, border: '1px solid #ccc', borderRadius: 8 }}>
          <p><strong>üìù ÊèèËø∞Ôºö</strong>{response.description}</p>
          <p><strong>üìÇ È°ûÂà•Ôºö</strong>{response.category}</p>
          <p><strong>üí∞ ÈáëÈ°çÔºö</strong>{response.amount}</p>
          <p><strong>üì• È°ûÂûãÔºö</strong>{response.type}</p>
        </div>
      )}

      <hr style={{ margin: '30px 0' }} />

      <h3>üìä Êü•Ë©¢ËàáÂàÜÊûê</h3>
      <div>
        Âπ¥Ôºö<select value={year} onChange={e => setYear(e.target.value)}>
          <option value="">ÂÖ®ÈÉ®</option>
          {[2023, 2024, 2025].map(y => <option key={y}>{y}</option>)}
        </select>
        ÊúàÔºö<select value={month} onChange={e => setMonth(e.target.value)}>
          <option value="">ÂÖ®ÈÉ®</option>
          {[...Array(12)].map((_, i) => <option key={i + 1}>{i + 1}</option>)}
        </select>
        Êó•Ôºö<select value={day} onChange={e => setDay(e.target.value)}>
          <option value="">ÂÖ®ÈÉ®</option>
          {[...Array(31)].map((_, i) => <option key={i + 1}>{i + 1}</option>)}
        </select>
        <button onClick={fetchRecords}>Êü•Ë©¢</button>
      </div>

      <p>üí∞ Êî∂ÂÖ•Ôºö{income} ÂÖÉ | üßæ ÊîØÂá∫Ôºö{expense} ÂÖÉ | üíµ Ê∑®È°çÔºö{total} ÂÖÉ</p>

      <PieChart width={400} height={300}>
        <Pie data={chartData} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={80} label>
          {chartData.map((entry, index) => (
            <Cell key={index} fill={getCategoryColor(entry.name)} />
          ))}
        </Pie>
        <Tooltip />
      </PieChart>

      <LineChart width={500} height={300} data={lineData}>
        <CartesianGrid stroke="#ccc" />
        <XAxis dataKey="date" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Line type="monotone" dataKey="amount" stroke="#8884d8" />
      </LineChart>

      <ul style={{ listStyle: 'none', padding: 0 }}>
