import React, { useRef, useState } from 'react';
import axios from 'axios';

const getCategoryColor = (category) => {
  const map = {
    '飲食': 'green',
    '交通': 'blue',
    '娛樂': 'purple',
    '購物': 'orange',
    '未分類': 'gray'
  };
  return map[category] || 'black';
};

const formatTime = (isoTime) => {
  const t = new Date(isoTime);
  return t.toLocaleString('zh-TW');
};

const VoiceBudgetApp = () => {
  const mediaRecorderRef = useRef(null);
  const [audioBlob, setAudioBlob] = useState(null);
  const [recording, setRecording] = useState(false);
  const [response, setResponse] = useState(null);
  const [records, setRecords] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [mode, setMode] = useState('day');

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const recorder = new MediaRecorder(stream);
    const chunks = [];
    recorder.ondataavailable = (e) => chunks.push(e.data);
    recorder.onstop = () => {
      const blob = new Blob(chunks, { type: 'audio/webm' });
      setAudioBlob(blob);
      uploadAudio(blob);
    };
    recorder.start();
    mediaRecorderRef.current = recorder;
    setRecording(true);
  };

  const stopRecording = () => {
    mediaRecorderRef.current.stop();
    setRecording(false);
  };

  const uploadAudio = async (blob) => {
    const formData = new FormData();
    formData.append("file", blob, "voice.webm");
    try {
      const res = await axios.post("http://localhost:8080/api/upload", formData);
      setResponse(res.data);
    } catch (err) {
      console.error("上傳失敗：", err);
      alert("上傳失敗！");
    }
  };

  const fetchRecords = async () => {
    try {
      const res = await axios.get(`http://localhost:8080/api/records?mode=${mode}`);
      setRecords(res.data.records);
      setTotalAmount(res.data.total);
    } catch (err) {
      console.error("查詢失敗：", err);
    }
  };

  return (
    <div style={{ padding: 20, maxWidth: 600, margin: 'auto' }}>
      <h2>🎤 語音記帳系統</h2>

      <button
        onMouseDown={startRecording}
        onMouseUp={stopRecording}
        style={{ padding: 10 }}>
        {recording ? '錄音中...' : '🎙 按住錄音'}
      </button>

      {response && (
        <div style={{ marginTop: 20 }}>
          <h3>✅ 紀錄成功</h3>
          <p><strong>📝 描述：</strong>{response.description || "(空白)"}</p>
          <p>
            <strong>📂 類別：</strong>
            <span style={{
              backgroundColor: getCategoryColor(response.category),
              color: "white",
              padding: "3px 8px",
              borderRadius: 5
            }}>
              {response.category}
            </span>
          </p>
          <p><strong>💰 金額：</strong>{response.amount} 元</p>
          <p><strong>📅 時間：</strong>{formatTime(response.time)}</p>
        </div>
      )}

      <hr style={{ margin: '30px 0' }} />

      <h3>📊 歷史查詢</h3>
      <div>
        <select value={mode} onChange={(e) => setMode(e.target.value)}>
          <option value="day">本日</option>
          <option value="month">本月</option>
          <option value="year">本年</option>
        </select>
        <button onClick={fetchRecords} style={{ marginLeft: 10 }}>查詢</button>
      </div>

      <div style={{ marginTop: 10 }}>
        <strong>💵 總消費：</strong>{totalAmount} 元
      </div>

      <ul style={{ listStyle: 'none', paddingLeft: 0 }}>
        {records.map(r => (
          <li key={r.id} style={{ margin: '10px 0', padding: 10, border: '1px solid #ccc', borderRadius: 5 }}>
            <p><strong>{r.description}</strong></p>
            <p>
              📂 <span style={{ color: getCategoryColor(r.category) }}>{r.category}</span> |
              💰 {r.amount} 元 |
              🕒 {formatTime(r.time)}
            </p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default VoiceBudgetApp;
